import React from "react";
import { useState, useRef } from "react";

function getChoicesRows(choices)
{
	let choicesRows = [];
	for (let i = 0; i < choices.length; i++) {
		let choice = choices[i];
		choicesRows.push(
			<li key={ choice.react_key }>
				<div>
				<label htmlFor="">Choice { i }</label>
				<input name="choice{ i }" type="" value="{ choice.choice_text }"/>
				</div>
			</li>
		);
	}
	return choicesRows;
}

function CreateQuestion()
{
	let [choices, setChoices] = useState([]);
	let questionInputRef = useRef()

	function handleAddChoice(e)
	{
		e.preventDefault();
		setChoices((prevState)=>{
			return prevState.push({
				"choice_text": "",
				"react_key": (Math.random() + 1).toString(36).substring(7),
			});
		});
	}

	let choicesRow = getChoicesRows(choices);
	return (
		<div className="createForm">
		<fieldset>
			<legend>
				<h2>Question Creation Form</h2>
			</legend>
			<form>
				<div className="createInput">
					<label htmlFor="question_text">Question Text:</label>
					<input
						name="question_text"
						type="text"
						required
						placeholder="Ex.: Why does javascript suck?"
						ref={ questionInputRef }
					/>
				</div>
				{ choicesRow }
				<button className="button edit large">Add a New Choice</button>
			</form>
		</fieldset>
		</div>
	);
}

export default CreateQuestion;
