import React from "react";

import { useState, useEffect } from "react";

import QuestionList from "../components/QuestionList";
import Question from "../components/Question";
import EditForm from "../components/EditForm";
import DeleteConfirmation from "../components/DeleteConfirmation";

import Modal from "../layout/Modal";
import Backdrop from "../layout/Backdrop";

import csrftoken from "../utils/csrf";

function MainPage()
{

	let [ choices, setChoices ] = useState([]);
	let [ questions, setQuestions ] = useState([])
	let [ toEditQuestion, setToEditQuestion ] = useState(null);

	let [ showModal, setShowModal] = useState(false);
	let [ showEdit, setShowEdit ] = useState(false);
	let [ showDelete, setShowDelete ] = useState(false);
	let [ pageLoading, setPageLoading ] = useState(true);

	useEffect(()=>{
		fetch("http://127.0.0.1:8000/polls/", {
			method: "GET",
			headers: {
				"Content-Type": "application/json",
				"X-CSRFToken": csrftoken,
			}
		}).then((res)=>{
			return res.json();
		}).then((data)=>{
			setQuestions(data);
			setPageLoading(false);
		});
	}, []);

	function handleShowModal()
	{
		setShowModal((prevState)=> {
			return !prevState;
		});

		setShowEdit((prevState)=> {
			if (prevState)
				return !prevState;
			return prevState;
		});

		setShowDelete((prevState)=> {
			if (prevState)
				return !prevState;
			return prevState;
		});
	}

	function handleOpenEdit(e)
	{
		let questionId = e.target.name;

		handleShowModal();
		fetch(`http://127.0.0.1:8000/polls/${questionId}`, {
			method: "GET",
			headers: {
				"Content-Type": "application/json",
				"X-CSRFToken": csrftoken,
			}
		}).then((res)=>{
			return res.json();
		}).then((data)=>{
			/* must call after show modal since show modal
			 * will call setShowEdit(false) if it finds it true
			 * in order to handle the closing logic
			 */
			setToEditQuestion(data.question);
			setChoices(data.choices);
			setShowEdit(true);
		});
	}

	function handleOpenDelete()
	{
		handleShowModal();
		/* must call after show modal since show modal
		 * will call setShowEdit(false) if it finds it true
		 * in order to handle the closing logic
		 */
		setShowDelete(true);
	}

	/*
	function handleEditSubmit()
	{
	}

	function handleDeleteConfirmation()
	{
	}
	*/

	let qcomps = questions.map((q) => {
		return (
				<Question key={ q.id }
				q={ q }
				onDelete={ handleOpenDelete }
				onEdit={ handleOpenEdit }
				/>
		);
	});

	let extraContent = (
		<React.Fragment>
		<Modal>
			{ showDelete ?
				<DeleteConfirmation onCancel={ handleShowModal } question={ questions[0] }/>
			:
			showEdit ?
				<EditForm
				question={ toEditQuestion }
				choices={ choices }
				onCancel= { handleShowModal }
				/>
			:
			<h1>Loading</h1>
			}
		</Modal>
		<Backdrop onClick={ handleShowModal } />
		</React.Fragment>
	);

	return (
		<React.Fragment>
			{ pageLoading ?
				<h1>Loading</h1>
			:
				<QuestionList>{ qcomps }</QuestionList>
			}
			{ showModal && extraContent }
		</React.Fragment>
	);
}

export default MainPage;
