import React from "react";

function EditForm(props)
{
	ccomps = props.choices.map((c)=>{
		return (
			<div className="voteRow" key={ c.id }>
				<span>c.choice_text</span>
				<button>+</button>
				<span>c.votes</span>
			</div>
		);
	});

	return (
		<fieldset>
			<legend>Votes for: { props.question.question_text }</legend>
			<div className="voteContainer">
				{ c.comps }
			</div>
		</fieldset>
	);
}

export default EditForm;
