import React from "react";
import { useState, useRef } from "react";

function getChoicesRows(choices, handleChoiceChange)
{
	let choicesRows = [];
	let i = 0
	for (const [key, choice] of Object.entries(choices)) {
		choicesRows.push(
			<li key={ key }>
				<div className="choiceInput">
					<label className="choiceLabel" htmlFor="choice{ i }">Choice { i }</label>
					<input
						onChange={ handleChoiceChange }
						name="{ key }"
						type="text"
						value={ choice.choice_text }
					/>
				</div>
			</li>
		);
		i++;
	}
	return choicesRows;
}

function CreateQuestion()
{
	let [choices, setChoices] = useState({});
	let questionInputRef = useRef()

	function handleAddChoice(e)
	{
		e.preventDefault();
		setChoices((prevState)=>{
			let key = (Math.random() + 1).toString(36).substring(7);
			return {
				...prevState,
				[key]: {
					"choice_text": "",
				}
			}

		});
	}

	function handleChoiceChange(e)
	{
		console.log(e.target.name);
		setChoices((prevState)=>{
			let key = e.target.name;
			return {
				...prevState,
				[key]: {
					"choice_text": e.target.value,
				}
			}
		});
	}

	let choicesRow = getChoicesRows(choices, handleChoiceChange);
	return (
		<div className="createForm">
		<fieldset>
			<legend>
				<h2>Question Creation Form</h2>
			</legend>
			<form>
				<label className="questionLabel" htmlFor="question_text">Question Text:</label>
				<input
					className="questionInput"
					name="question_text"
					type="text"
					required
					placeholder="Ex.: Why does javascript suck?"
					ref={ questionInputRef }
				/>
				{ choicesRow }
				<button className="button edit large" onClick={ handleAddChoice }>Add a New Choice</button>
			</form>
		</fieldset>
		</div>
	);
}

export default CreateQuestion;
